    item.UseItem();
    await Coroutine.Wait(6000, () => !Core.Me.IsCasting);
  }

  if (item.RawItemId == 27358) {  item.Move(_weaponSlot);  }
}

foreach(var item in InventoryManager.FilledInventoryAndArmory) {  if (item.RawItemId == 25217) {  item.Move(_crystalSlot);  }  }

